    <script>
        const pricePerDay = 250000; // Define price per day

        function updatePrice() {
            const duration = document.getElementById('duration').value;
            const days = parseInt(duration.split(' ')[0]);
            const totalPrice = days * pricePerDay;
            document.getElementById('price').value = `Rp ${totalPrice.toLocaleString()}`;
        }

        function addVehicle() {
            const no = document.getElementById('no').value;
            const image = document.getElementById('image').value;
            const type = document.getElementById('type').value;
            const plate = document.getElementById('plate').value;
            const price = document.getElementById('price').value;
            const duration = document.getElementById('duration').value;
            const renterName = document.getElementById('renterName').value;
            const renterContact = document.getElementById('renterContact').value;
            const renterId = document.getElementById('renterId').value;

            if (no && image && type && plate && price && duration && renterName && renterContact && renterId) {
                const tableBody = document.getElementById('vehicleTableBody');
                
                const row = document.createElement('tr');
                row.innerHTML = `
                    <td>${no}</td>
                    <td><img src="${image}" alt="${type}"></td>
                    <td>${type}</td>
                    <td>${plate}</td>
                    <td>${duration}</td>
                    <td>${price}</td>
                    <td>${renterName}</td>
                    <td>${renterContact}</td>
                    <td>${renterId}</td>
                `;

                tableBody.appendChild(row);

                // Clear the form fields after adding
                document.getElementById('vehicleForm').reset();
                document.getElementById('price').value = ''; // Clear price field

                // Remove any existing alerts
                const alertContainer = document.getElementById('alertContainer');
                if (alertContainer) {
                    alertContainer.innerHTML = '';
                }
            } else {
                showAlert('Harap lengkapi semua field.');
            }
        }

        function showAlert(message) {
            const alertContainer = document.getElementById('alertContainer');
            if (alertContainer) {
                alertContainer.innerHTML = `
                    <div class="alert">${message}</div>
                `;
            }
        }

        function goBack() {
            window.history.back();
        }
    </script>
